void displayBlack()
{
	// clears whole screen
	fillRectangle(0,0,128,160,0);
	fillRectangle(40,0,45,179,0);
	fillRectangle(0,65,128,20,0);
}//end display black

void Menu()
{	
	uint16_t x = 50;
	uint16_t y = 50;

	score = 0;
	level2score = 0;
	level3score = 0;
	// Start game menu
	while (1)
	{
		// Displays the title
		printText("SLICE NINJA", 25, 30, RGBToWord(0,0xff,0), 0);
		printText("Press down", 25, 70, RGBToWord(0,0xff,0), 0);
		printText("to start", 25, 90, RGBToWord(0,0xff,0), 0);
		putImage(50,125,24,36,standingninja,0,vinverted);
		// level one begins when down is pressed
		if ( (GPIOA->IDR & (1 << 11)) == 0) // down pressed
		{
			displayBlack();
			level1();
		}//end if 
	}//end while

}//end start screen

void level1()
{
	start=0;

	printTextX2("LEVEL 1", 10, 100, RGBToWord(0,0xff,0), 0);
	delay(350);
	displayBlack();
	
	while(1)
	{
		putImage(10,strawberry_y,24,36,strawberry,0,0);
		if(strawDelay==4)
		{
			strawberry_y = strawberry_y+1;
			strawDelay=0;
		}
		else
		{
			strawDelay=strawDelay+1;
			
		}
		if(strawberry_y > 123)
		{
			fillRectangle(10,strawberry_y,24,36,0);
			strawberry_y = 0;
		}
		if (isInside(10,strawberry_y,24,36,x,y) || isInside(10,strawberry_y,24,36,x+12,y) || isInside(10,strawberry_y,24,36,x,y+16) || isInside(10,strawberry_y,24,36,x+12,y+16) )
		{	
			fillRectangle(10,88,24,36,0);
			strawberry_y = 0;
			score = score + 1;

	
		}//if collision with strawberry, score increments

		putImage(40,ninjastar_y,30,30,ninjastar,0,0);
		if(starDelay==8)
		{
			ninjastar_y = ninjastar_y+1;
			starDelay=0;
		}
		else
		{
			starDelay=starDelay+1;
			
		}
		if(ninjastar_y > 123)
		{
			fillRectangle(40,ninjastar_y,24,36,0);
			ninjastar_y= 0;
		}
		if (isInside(40,ninjastar_y,24,36,x,y) || isInside(40,ninjastar_y,24,36,x+12,y) || isInside(40,ninjastar_y,24,36,x,y+16) || isInside(40,ninjastar_y,24,36,x+12,y+16) )
		{	
			fillRectangle(40,88,24,36,0);
			ninjastar_y= 0;
			score = 0;
			restart();
		}//if the ninja star hits the ninja, game ends

		//falling cherry 
		putImage(70,cherry_y,24,36,cherry,0,0);
		if(cherryDelay==10)
		{
			cherry_y = cherry_y+1;
			cherryDelay=0;
		}
		else
		{
			cherryDelay=cherryDelay+1;
		}
		if(cherry_y > 123)
		{
			fillRectangle(70,cherry_y,24,36,0);
			cherry_y = 0;
		}
		if (isInside(70,cherry_y,24,36,x,y) || isInside(70,cherry_y,24,36,x+12,y) || isInside(70,cherry_y,24,36,x,y+16) || isInside(70,cherry_y,24,36,x+12,y+16) )
		{	
			fillRectangle(70,88,24,36,0);
			cherry_y = 0;
			score = score + 1;
			
		}

		//falling watermelon
		putImage(100,watermelon_y,24,36,watermelon,0,0);
		if(waterDelay == 15)
		{
			watermelon_y = watermelon_y+1;
			waterDelay=0;
		}
		else
		{
			waterDelay=waterDelay+1;
		}
		if(watermelon_y > 123)
		{
			fillRectangle(100,watermelon_y,24,36,0);
			watermelon_y = 0;
		}
		if (isInside(100,watermelon_y,24,36,x,y) || isInside(100,watermelon_y,24,36,x+12,y) || isInside(100,watermelon_y,24,36,x,y+16) || isInside(100,watermelon_y,24,36,x+12,y+16) )
		{	
			fillRectangle(100,88,24,36,0);
			watermelon_y = 0;
			score = score + 1;
		}
		
		if(score == 3)
		{
			displayBlack();
			printTextX2("Level 1", 30, 60, RGBToWord(0x80,0xFF,0x80), 0);
			printTextX2("Completed!!", 10, 90, RGBToWord(0x80,0xFF,0x80), 0);
			printText("Score: ", 0, 110, RGBToWord(0xff,0xff,0xff), 0);
			printNumber(score, 40, 110, RGBToWord(0xff,0xff,0xff), 0);
			delay(600);
			displayBlack();
			level2();
		}

		hmoved = vmoved = 0;
		//hinverted = vinverted = 0;
		if ((GPIOB->IDR & (1 << 4))==0) // right pressed
		{					
			if (x < 110)
			{
			
				x = x + 1;
				hmoved = 1;
				hinverted=0;
			}						
		}
		if ((GPIOB->IDR & (1 << 5))==0) // left pressed
		{			
			
			if (x > 10)
			{
				x = x - 1;
				hmoved = 1;
				hinverted=1;
			}			
		}

		if ((vmoved) || (hmoved))
		{
			// only redraw if there has been some movement (reduces flicker)
			fillRectangle(oldx,oldy,24,36,0);
			
			if (hmoved)
			{
				if (toggle)
					putImage(x,y,24,36,rightninja,hinverted,0);
				else
					putImage(x,y,24,36,walkninja,hinverted,0);
				
				toggle = toggle ^ 1;

				//keep at bottom of screen 
				if( x < 1)
				{
					x = 1;
				}
				if(x > 91)
				{
					x = 91;
				}
			}
			else
			{
				putImage(x,y,24,36,standingninja,0,vinverted);
			}
		}
		
		if (attackPress())
		{
			if (toggle)
			putImage(x,y,24,36,attackninja,hinverted,vinverted);
			else
			putImage(x,y,24,36,rightninja,hinverted,vinverted);
			
			toggle = toggle ^ 1;

			//keep at bottom of screen
			if( x < 1)
			{
				x = 1;
			}
			if(x > 91)
			{
				x = 91;
			}
		}	
	}//end while
	delay(50);

}//end level 1

